package dp;

public class 字符串一次编辑距离 {
	
	public static int min(int i, int j) {
		return (i < j) ? i : j;
	}
	
	// 二维数组导致空间复杂度过大
	public static boolean DP(char[] src, char[] tar) {
		int srcLen = src.length;
		int tarLen = tar.length;
        if (Math.abs(srcLen - tarLen) > 1){
            return false;
        }
		int[][] dp = new int[srcLen + 1][tarLen + 1];
		// initialize
		for (int i = 0; i <= srcLen; i++) {
			dp[i][0] = i;
		}
		for (int j = 0; j <= tarLen; j++) {
			dp[0][j] = j;
		}
		// 从第1个字符开始
		for (int i = 1; i <= srcLen; i++) {
			for (int j = 1; j <= tarLen; j++) {
				if (src[i - 1] == tar[j - 1]) {
					dp[i][j] = dp[i - 1][j - 1]; 
				}
				else {
					dp[i][j] = min(min(dp[i - 1][j], dp[i][j - 1]), dp[i - 1][j - 1]) + 1; 
				}
			}
		}
		return (dp[srcLen][tarLen] == 1) ? true : false;
	}
	
	// 分析法
	public static boolean analysis(String s, String t) {
		if (s.equals(t)) {
			return false;
		}
		int srcLen = s.length();
		int tarLen = t.length();
		int diff = Math.abs(srcLen - tarLen);
        if (diff > 1){
            return false;
        } 
        // 长度相差1, 只能进行删除或者插入操作
        else if (diff == 1) {
        	/* 两个字符串的起点放一个指针, 若相同则同时+1, 不相同则长的串+1
        	 * 最后看短的串的指针是否等于它的长度
        	 */
        	int i = 0, j =0;
        	// 源串长
        	if (srcLen > tarLen) {
        		while (i < srcLen && j < tarLen) {
    				if (s.charAt(i) == t.charAt(j)) {
    					i++;
    					j++;
    				} else {
    					i++;
    				}
    			}
            	return (j == tarLen) ? true : false;				
			} 
        	// 目标串长
        	else {
				while (i < srcLen && j < tarLen) {
					if (s.charAt(i) == t.charAt(j)) {
						i++;
						j++;
					} else {
						j++;
					}
				}
	        	return (i == srcLen) ? true : false;
			}
		} 
        // 长度相等, 只能进行替换操作. 直接找不相等的个数
        else {
        	int i = 0, j =0, match = 0;
        	while (i < srcLen && j < tarLen) {
				if (s.charAt(i) == t.charAt(j)) {
					i++;
					j++;
				} else {
					match++;
					i++;
					j++;
				}
			}
        	return (match == 1) ? true : false;
		}
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String src = "";
		String tar = "";
		System.out.println(DP(src.toCharArray(), tar.toCharArray()));
		System.out.println(analysis(src, tar));
	}
}
